import React, { useState } from 'react';
import teams from '../assets/teams';

const PredictionPage = ({history}) => {
    const [homeTeam, setHomeTeam] = useState("")
    const [awayTeam, setAwayTeam] = useState("")
    const [result, setResult] = useState("")

    useEffect(() => {
        getResult()
    }, [])

    let predictResult = async () => {
        fetch('http://127.0.0.1:8000/api/predict/', {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                'home_team': homeTeam,
                'away_team': awayTeam
            })
        })
    }


    let getResult = async () => {
        let response = await fetch('http://127.0.0.1:8000/api/predict/')
        let data = await response.json()
        setResult(data)
    }


    let handleSubmit = () => {
        predictResult()
        history.push('/')
    }


    return <div>
            <div  className='select-menu'>
                <select className='form-select' onChange={(e)=>{
                    const selectedHomeTeam=e.target.value;
                    setHomeTeam(selectedHomeTeam)
                }}>
                    {teams.map(team => (
                        <option value={team.team}>{team.team}</option>
                    ))}                
                </select>

            </div>

            <div  className='select-menu'>
                <select className='form-select' onChange={(e)=>{
                    const selectedawayTeam=e.target.value;
                    setAwayTeam(selectedawayTeam)
                }}>
                    {teams.map(team => (
                        <option value={team.team}>{team.team}</option>
                    ))}
                </select>
            </div>
            <div className='select-menu'>
                <button className='btn btn-danger' onClick={handleSubmit}>Predict</button>
            </div>
         </div>;
    };

export default PredictionPage;
