import React from 'react';

const Instruxtions = () => {
  return <div>
      <br />
      <h3 className='text-danger'>Instructions</h3>
      <br />
      <div className='text-secondary'>
          <p>* Select two teams that are going to play against each other.</p>
          <p>* Click on the predict button to see the predicted result.</p>
          <p>* You will be able see a pie chart that shows probabilities of winning for the teams.</p>
          <p>* Predictions are more accurate 5 minutes before the match.</p>
          <p>* There is a link for analysis to compare the statistics of previous matches.</p>
          <p>* Click on that link to view analysis page.</p>
      </div>
  </div>;
};

export default Instruxtions;
