import React, { useState, PureComponent } from 'react';
import teams from '../assets/teams';
import { PieChart, Pie, Legend, Tooltip } from "recharts";





const PredictionPage = ({history}) => {
    let [homeTeam, setHomeTeam] = useState("")
    let [awayTeam, setAwayTeam] = useState("")
    let [result, setResult] = useState("")





    let predictResult = async () => {
        fetch('http://127.0.0.1:8000/api/predict/', {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "home_team": homeTeam,
                "away_team": awayTeam
            })
        }).then(function(response) {
            return response.json();
           })
           .then(function(data) {
            console.log('Result:', data)
            setResult(data);
           })

        const data01 = [
            { name: result.home_team, value: result.probability.home_team*100 },
            { name: result.away_team, value: result.probability.home_team*100 },
            { name: "Draw", value: result.probability.draw},
          ];


        return <div>
        <div className='status'>
            <p className='lead'>{result.result}</p>
        </div>
        <div>
        <PieChart width={1000} height={400}>
            <Pie
                dataKey="value"
                isAnimationActive={false}
                data={data01}
                cx={200}
                cy={200}
                outerRadius={80}
                fill="#8884d8"
                label
            />
            <Tooltip />
        </PieChart>
    </div>
    </div>
    }

    let handleSubmit = () => {
        predictResult()
        history.push('/')
    }



    return <div>
            <div  className='select-menu'>
                <select className='form-select' onChange={(e)=>{
                    const selectedHomeTeam=e.target.value;
                    setHomeTeam(selectedHomeTeam)
                }}>
                    {teams.map(team => (
                        <option value={team.team}>{team.team}</option>
                    ))}                
                </select>

            </div>

            <div  className='select-menu'>
                <select className='form-select' onChange={(e)=>{
                    const selectedawayTeam=e.target.value;
                    setAwayTeam(selectedawayTeam)
                }}>
                    {teams.map(team => (
                        <option value={team.team}>{team.team}</option>
                    ))}
                </select>
            </div>
            <div className='select-menu'>
                <button className='btn btn-danger' onClick={handleSubmit}>Predict</button>
            </div>

            <div className='status'>
                <p className='lead'>{result.result}</p>
            </div>   
         </div>;
    };

export default PredictionPage;
