import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { Line, Pie, Bar } from 'react-chartjs-2';

import * as ReactBootStrap from 'react-bootstrap'

import 'bootstrap/dist/css/bootstrap.css';
import Carousel from 'react-bootstrap/Carousel';

import AnalysisHeader from '../components/AnalysisHeader'
import Spinner from '../components/Spinner'

import AnalysisInst from '../components/AnalysisInst'

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  BarElement,
);



const AnalysisPage = ({history}) => {

  let [loading, setLoading] = useState(false)
  let [homeTeam, setHomeTeam] = useState("")
  let [awayTeam, setAwayTeam] = useState("")
  let [homeStats, setHomeStats] = useState([])
  let [awayStats, setAwayStats] = useState([])
  let [years, setYears] = useState([])
  let [headtToHead, setHeadToHead] = useState([])
  let [polarity, setPolarity] = useState([])

    useEffect(() => { 
    getData()
    analysisResult() 
    }, [])

    let getData = () => {
      homeTeam = localStorage.getItem('homeTeam', homeTeam)
      awayTeam = localStorage.getItem('awayTeam', awayTeam)
      setHomeTeam(homeTeam)
      setAwayTeam(awayTeam)
    }



  const data01 = {
      labels: years,
      datasets: [
        {
          label: homeTeam,
          data: homeStats,
          fill: false,
          borderColor: "rgba(75,192,192,1)"
        },
        {
          label:awayTeam,
          data: awayStats,
          fill: false,
          borderColor: "#494d4f"
        }
      ]
    };

    const data02 = {
      labels: [homeTeam, awayTeam],
      datasets: [
        {
          label: '# of Votes',
          data: headtToHead,
          backgroundColor: [
            "rgba(75,192,192,1)",
            "#494d4f"
          ],

          borderWidth: 1,
        },
      ],
    };
    const labels = [homeTeam, awayTeam]
    const data03 = {
      labels,
      datasets: [
        {
          label: 'Polarity',
          data: polarity,
          backgroundColor: '#00aba9',
        },

      ],
    };





  let analysisResult = async () => {
    setLoading(true)
    fetch('/api/analysis/', {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            "home_team": homeTeam,
            "away_team": awayTeam
        })
    }).then(function(response) {
      return response.json();
     })
     .then(function(data) {
      setHomeStats(data.home_win_stats)
      setAwayStats(data.away_win_stats)
      setYears(data.years)
      setHeadToHead(data.count)
      setPolarity(data.polarity)
      setLoading(false)

     })
}


  return <div>
    {loading ? (<Spinner />): [
        <div>
            <AnalysisHeader />

          <Carousel className='carousel'>
            <Carousel.Item interval={1500}>
                <h3 className='analysis-1'>Performance over Years</h3>
                <div className="line-diagram">
                  <Line data={data01} />
                </div>
                </Carousel.Item>

              <Carousel.Item interval={1500}>
                <h3 className='analysis-1'>Head to Head Analysis</h3>
                {headtToHead.length == 0 ? (<div className='pie-diagram'>
                    <Pie data={data02} />
                  </div>):(
                  <h4 className='text-secondary'>These teams did not play against each other.</h4>
                  )}  
              </Carousel.Item>
              <Carousel.Item interval={1500}>
                <h3 className='analysis-1'>Support on Twitter</h3>
                <div className='line-diagram'>
                  <Bar data={data03} />
                </div>
              </Carousel.Item>
          </Carousel>

          <AnalysisInst />

      </div>
    ]}


    </div>

};

export default AnalysisPage;
